import React, { useState } from "react";
import { Card, Modal, Button } from "react-bootstrap";
import "./App.css";
import sunny from "./images/sunny.png";
import cloudy from "./images/cloudy.png";
import rainy from "./images/rain.png";
import snow from "./images/snow.png";
import tornado from "./images/tornado.png";
import thunder from "./images/thunder.png";
import windy from "./images/windy.png";
import arrow from "./images/arrow.png";
import humidity from "./images/humidity.png";
const api = {
  key: "67b10bbf46e6fecb07c0365d103c7e1f",
  base: "https://api.openweathermap.org/data/2.5/"
};

function App() {
  const [modalShow, setModalShow] = React.useState(false);
  const [weather, setWeather] = useState({});
  console.log(coord);
  const dateBuilder = d => {
    let months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December"
    ];
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday"
    ];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  const weatherStatus =
    typeof weather.main != "undefined" ? weather.weather[0].main : null;

  let checkWeather = name => {
    switch (name) {
      case "Clear":
        return sunny;
        break;
      case "Clouds":
        return cloudy;
        break;
      case "Rain":
        return rainy;
        break;
      case "Snow":
        return snow;
        break;
      case "Thunderstorm":
        return thunder;
        break;
      case "Windy":
        return windy;
        break;
      case "Tornado":
        return tornado;
        break;
      default:
        return;
    }
  };

  return (
    <>
      {
        <div class="container">
          <div class="weather-side">
            <div class="weather-gradient"></div>
            <div class="date-container">
              <h2 class="date-dayname">Tuesday</h2>
              <span class="date-day">15 Jan 2019</span>
              <i class="location-icon" data-feather="map-pin"></i>
              <span class="location">Paris, FR</span>
            </div>
            <div class="weather-container">
              <i class="weather-icon" data-feather="sun"></i>
              <h1 class="weather-temp">29°C</h1>
              <h3 class="weather-desc">Sunny</h3>
            </div>
          </div>
          <div class="info-side">
            <div class="today-info-container">
              <div class="today-info">
                <div class="precipitation">
                  {" "}
                  <span class="title">PRECIPITATION</span>
                  <span class="value">0 %</span>
                  <div class="clear"></div>
                </div>
                <div class="humidity">
                  {" "}
                  <span class="title">HUMIDITY</span>
                  <span class="value">34 %</span>
                  <div class="clear"></div>
                </div>
                <div class="wind">
                  {" "}
                  <span class="title">WIND</span>
                  <span class="value">0 km/h</span>
                  <div class="clear"></div>
                </div>
              </div>
            </div>
            <div class="week-container">
              <ul class="week-list">
                <li class="active">
                  <i class="day-icon" data-feather="sun"></i>
                  <span class="day-name">Tue</span>
                  <span class="day-temp">29°C</span>
                </li>
                <li>
                  <i class="day-icon" data-feather="cloud"></i>
                  <span class="day-name">Wed</span>
                  <span class="day-temp">21°C</span>
                </li>
                <li>
                  <i class="day-icon" data-feather="cloud-snow"></i>
                  <span class="day-name">Thu</span>
                  <span class="day-temp">08°C</span>
                </li>
                <li>
                  <i class="day-icon" data-feather="cloud-rain"></i>
                  <span class="day-name">Fry</span>
                  <span class="day-temp">19°C</span>
                </li>
                <div class="clear"></div>
              </ul>
            </div>
            <div class="location-container">
              <button
                onClick={() => setModalShow(true)}
                class="location-button"
              >
                {" "}
                <i data-feather="map-pin"></i>
                <span>Change location</span>
              </button>
            </div>
          </div>
        </div>
      }
      <MyModal show={modalShow} onHide={() => setModalShow(false)} />
    </>
  );
  function MyModal(props) {
    console.log(props);
    const [query, setQuery] = useState("");
    const [coord, setCoord] = useState({});

    async function fetchCoords() {
      const response = await fetch(
        `${api.base}weather?q=${query}&units=imperial&APPID=${api.key}`
      );
      const json = await response.json();
      setCoord(json.coord);
      setQuery("");
    }

    const search = evt => {
      if (evt.key === "Enter") {
        fetchCoords();
      }
    };

    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        style={{ position: "absolute", background: "#e5e5e5" }}
      >
        <Modal.Header style={{ textAlign: "right" }}>
          <Button
            style={{
              background: "transparent",
              border: "none",
              color: "red",
              fontSize: "1.5rem",
              fontWeight: "bold",
              margin: "3px"
            }}
            onClick={props.onHide}
          >
            X
          </Button>
        </Modal.Header>
        <Modal.Title
          style={{ fontSize: "2.5rem", textAlign: "center" }}
          id="contained-modal-title-vcenter"
        >
          Choose a city ?
        </Modal.Title>
        <Modal.Body style={{ textAlign: "center", margin: "3rem" }}>
          <input
            style={{
              fontSize: "1.5rem",
              borderRadius: "25px",
              outline: "none"
            }}
            type="text"
            className="search-box"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </Modal.Body>
      </Modal>
    );
  }
}

export default App;
