import React, { useState, useEffect } from "react";
import axios from "axios";
import { Card, Modal, Button } from "react-bootstrap";
import "./App.css";
import sunny from "./images/sunny.png";
import cloudy from "./images/cloudy.png";
import rainy from "./images/rain.png";
import snow from "./images/snow.png";
import tornado from "./images/tornado.png";
import thunder from "./images/thunder.png";
import windy from "./images/windy.png";
import arrow from "./images/arrow.png";
import humidity from "./images/humidity.png";
import CircleLoader from "react-spinners/CircleLoader";
const api = {
  key: "67b10bbf46e6fecb07c0365d103c7e1f",
  base: "https://api.openweathermap.org/data/2.5/"
};

const cityData = {
  base: "https://api.openweathermap.org/data/2.5/onecall?"
};

function MyModal(props) {
  const [query, setQuery] = useState("");
  const [coord, setCoord] = useState({ lat: 47.61, lon: -122.33 });
  const setCurrent = props.setCurrent;
  const show = props.setModal;
  const setWeather = props.setWeather;

  async function fetchCoords() {
    const response = await fetch(
      `${api.base}weather?q=${query}&units=imperial&APPID=${api.key}`
    );
    const json = await response.json();
    setCurrent(json);
    setCoord(json.coord);
    setQuery("");
  }
  useEffect(() => {
    if (coord) {
      async function fetchCityData() {
        const response = await fetch(
          `${cityData.base}lat=${coord.lat}&lon=${coord.lon}&appid=${api.key}`
        );
        const json = await response.json();
        setWeather(json);
      }
      fetchCityData();
    }
    return;
  }, [coord]);
  const search = evt => {
    if (evt.key === "Enter") {
      fetchCoords();
      show(false);
    }
  };

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
      style={{ position: "absolute", background: "white" }}
    >
      <Modal.Header style={{ textAlign: "right" }}>
        <Button
          style={{
            background: "transparent",
            border: "none",
            color: "red",
            fontSize: "1.5rem",
            fontWeight: "bold",
            margin: "3px",
            cursor: "pointer"
          }}
          onClick={props.onHide}
        >
          X
        </Button>
      </Modal.Header>
      <Modal.Title
        style={{ fontSize: "2rem", textAlign: "center" }}
        id="contained-modal-title-vcenter"
      >
        Choose a city ?
      </Modal.Title>
      <Modal.Body style={{ textAlign: "center", margin: "3rem" }}>
        <input
          style={{
            fontSize: "1.2rem",
            borderRadius: "25px",
            outline: "none",
            height: "40px",
            textAlign: "center"
          }}
          type="text"
          className="search-box"
          placeholder="Your city here..."
          onChange={e => setQuery(e.target.value)}
          value={query}
          onKeyPress={search}
        />
      </Modal.Body>
    </Modal>
  );
}

function App(props) {
  const [current, setCurrent] = useState(null);
  const [modalShow, setModalShow] = React.useState(false);
  const [weather, setWeather] = useState({});

  useEffect(() => {
    axios
      .get(`${api.base}weather?q=Seattle&units=imperial&APPID=${api.key}`)
      .then(res => setCurrent(res.data));
  }, []);
  const dateBuilder = d => {
    let months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December"
    ];
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
