import React, { useState } from "react";
import { Card } from "react-bootstrap";
import "./App.css";
import sunny from "./images/sunny.png";
import cloudy from "./images/cloudy.png";
import rainy from "./images/rain.png";
import snow from "./images/snow.png";
import tornado from "./images/tornado.png";
import thunder from "./images/thunder.png";
import windy from "./images/windy.png";
import arrow from "./images/arrow.png";
import humidity from "./images/humidity.png";
const api = {
  key: "67b10bbf46e6fecb07c0365d103c7e1f",
  base: "https://api.openweathermap.org/data/2.5/"
};

function App() {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=imperial&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery("");
          console.log(result);
        });
    }
  };

  const dateBuilder = d => {
    let months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December"
    ];
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday"
    ];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };
  console.log(weather);

  const weatherStatus =
    typeof weather.main != "undefined" ? weather.weather[0].main : null;

  let checkWeather = name => {
    switch (name) {
      case "Clear":
        return sunny;
        break;
      case "Clouds":
        return cloudy;
        break;
      case "Rain":
        return rainy;
        break;
      case "Snow":
        return snow;
        break;
      case "Thunderstorm":
        return thunder;
        break;
      case "Windy":
        return windy;
        break;
      case "Tornado":
        return tornado;
        break;
      default:
        return;
    }
  };

  return (
    <div class="container">
      <div class="weather-side">
        <div class="weather-gradient"></div>
        <div class="date-container">
          <h2 class="date-dayname">Tuesday</h2>
          <span class="date-day">15 Jan 2019</span>
          <i class="location-icon" data-feather="map-pin"></i>
          <span class="location">Paris, FR</span>
        </div>
        <div class="weather-container">
          <i class="weather-icon" data-feather="sun"></i>
          <h1 class="weather-temp">29°C</h1>
          <h3 class="weather-desc">Sunny</h3>
        </div>
      </div>
      <div class="info-side">
        <div class="today-info-container">
          <div class="today-info">
            <div class="precipitation">
              {" "}
              <span class="title">PRECIPITATION</span>
              <span class="value">0 %</span>
              <div class="clear"></div>
            </div>
            <div class="humidity">
              {" "}
              <span class="title">HUMIDITY</span>
              <span class="value">34 %</span>
              <div class="clear"></div>
            </div>
            <div class="wind">
              {" "}
              <span class="title">WIND</span>
              <span class="value">0 km/h</span>
              <div class="clear"></div>
            </div>
          </div>
        </div>
        <div class="week-container">
          <ul class="week-list">
            <li class="active">
              <i class="day-icon" data-feather="sun"></i>
              <span class="day-name">Tue</span>
              <span class="day-temp">29°C</span>
            </li>
            <li>
              <i class="day-icon" data-feather="cloud"></i>
              <span class="day-name">Wed</span>
              <span class="day-temp">21°C</span>
            </li>
            <li>
              <i class="day-icon" data-feather="cloud-snow"></i>
              <span class="day-name">Thu</span>
              <span class="day-temp">08°C</span>
            </li>
            <li>
              <i class="day-icon" data-feather="cloud-rain"></i>
              <span class="day-name">Fry</span>
              <span class="day-temp">19°C</span>
            </li>
            <div class="clear"></div>
          </ul>
        </div>
        <div class="location-container">
          <button class="location-button">
            {" "}
            <i data-feather="map-pin"></i>
            <span>Change location</span>
          </button>
        </div>
      </div>
    </div>
    // <div id="container" style={{ display: "flex", height: "100vh" }}>
    //   <Card
    //     style={{
    //       width: "28rem",
    //       textAlign: "center",
    //       margin: "0 auto",
    //       paddingTop: "5rem"
    //     }}
    //   >
    //     <Card.Body>
    //       <Card.Title style={{ fontSize: "2rem" }}>Choose a city ?</Card.Title>
    //       <div className="container">
    //         <div class="search-box">
    //           <input
    //             type="text"
    //             className="search-box"
    //             placeholder="Search..."
    //             onChange={e => setQuery(e.target.value)}
    //             value={query}
    //             onKeyPress={search}
    //           />
    //           <span></span>
    //         </div>
    //       </div>
    //       <Card.Text>
    //         {typeof weather.main != "undefined" ? (
    //           <div
    //             style={{
    //               marginTop: "5rem",
    //               fontSize: "1rem",
    //               fontWeight: "bolder"
    //             }}
    //           >
    //             <div className="location-box">
    //               <Card.Img
    //                 style={{ width: "200px" }}
    //                 src={checkWeather(weatherStatus)}
    //               />
    //               <div className="location">
    //                 {weather.name}, {weather.sys.country}
    //               </div>
    //               <div className="date">{dateBuilder(new Date())}</div>
    //             </div>
    //             <div className="weather-box">
    //               <div className="temp">
    //                 {Math.round(weather.main.temp)}°F | feels like{" "}
    //                 {weather.main.feels_like}
    //               </div>
    //               <div className="weather">{weather.weather[0].main}</div>
    //               <div>
    //                 <img style={{ width: "10px" }} src={arrow} />{" "}
    //                 {weather.main.temp_max} °F
    //               </div>
    //               <div>
    //                 <img
    //                   style={{ width: "10px", transform: "rotate(180deg)" }}
    //                   src={arrow}
    //                 />{" "}
    //                 {weather.main.temp_min} °F
    //               </div>
    //               <div>
    //                 <img style={{ width: "30px" }} src={humidity} />
    //                 {weather.main.humidity}
    //               </div>
    //             </div>
    //           </div>
    //         ) : (
    //           ""
    //         )}
    //       </Card.Text>
    //     </Card.Body>
    //   </Card>
    // </div>
  );
}

export default App;
