import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import { Button, Card, Modal } from "react-bootstrap";

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h4>Centered Modal</h4>
        <p>
          Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
          dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
          consectetur ac, vestibulum at eros.
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

///////
function App() {
  //this is what opens a popup//
  const [modalShow, setModalShow] = React.useState(false);
  //
  const [results, setResults] = useState();
  useEffect(() => {
    axios
      .get(
        "https://api.openweathermap.org/data/2.5/onecall?lat=47.608013&lon=-122.335167&appid=67b10bbf46e6fecb07c0365d103c7e1f"
      )
      .then(res => setResults(res.data))
      .catch(err => console.log(err));
  }, []);
  console.log(results);
  return (
    <div
      className="App"
      style={{ display: "flex", flexWrap: "wrap", width: "900px" }}
    >
      {!results ? (
        <div>Loading...</div>
      ) : (
        results.daily.map(day => (
          <Card
            style={{ width: "18rem", border: "2px solid red", margin: "20px" }}
          >
            <Card.Img variant="top" src="holder.js/100px180" />
            <Card.Body>
              <Card.Title>Card Title</Card.Title>
              <Card.Text>
                <h3></h3>
              </Card.Text>
              <Button variant="primary" onClick={() => setModalShow(true)}>
                Launch vertically centered modal
              </Button>
            </Card.Body>
          </Card>
        ))
      )}
      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </div>
  );
}

export default App;
